CREATE TABLE `users` ( `id` int NOT NULL PRIMARY KEY AUTO_INCREMENT, `email` varchar(100) NOT NULL, `name` varchar(50) NOT NULL, `password` varchar(50) NOT NULL, `level` int NOT NULL DEFAULT 1, `job` varchar(20) NOT NULL DEFAULT 'none', `course_ing` int NOT NULL DEFAULT 0, `course_ed` int NOT NULL DEFAULT 0, `skillcount` int NOT NULL DEFAULT 0 );
CREATE TABLE `user_course_circumstances` ( `userID` int NOT NULL PRIMARY KEY, `front-end` int NOT NULL DEFAULT 0, `server` int NOT NULL DEFAULT 0, `C-DEV` int NOT NULL DEFAULT 0, `DB-MNG` int NOT NULL DEFAULT 0 );
CREATE TABLE `user_front_circumstances` ( `userID` int NOT NULL PRIMARY KEY, `HTML` int NOT NULL DEFAULT 0, `CSS` int NOT NULL DEFAULT 0, `JS` int NOT NULL DEFAULT 0 );
CREATE TABLE `codes` ( `id` int NOT NULL PRIMARY KEY AUTO_INCREMENT, `userID` int NOT NULL , `subject` int NOT NULL, `associated` int NOT NULL DEFAULT 0, `title` varchar(50) NOT NULL, `code` LONGTEXT NOT NULL, `lines` int NOT NULL, `comments` int NOT NULL DEFAULT 0, `recommends` int NOT NULL DEFAULT 0);
CREATE TABLE `HTML` ( `id` int NOT NULL PRIMARY KEY, `course_id` int NOT NULL, `title` varchar(100) NOT NULL, `desc` text NOT NULL, `code` text, `img_count` int NOT NULL, `has_coding` varchar(10) NOT NULL);
CREATE TABLE `HTML_Coding` ( `course_id` int NOT NULL PRIMARY KEY, `instruction` text NOT NULL );
CREATE TABLE `user_HTML_circumstances` ( `user_id` int NOT NULL PRIMARY KEY, `1` int NOT NULL DEFAULT 0, `2` int NOT NULL DEFAULT 0, `3` int NOT NULL DEFAULT 0, `4` int NOT NULL DEFAULT 0, `5` int NOT NULL DEFAULT 0, `6` int NOT NULL DEFAULT 0, `7` int NOT NULL DEFAULT 0, `8` int NOT NULL DEFAULT 0, `9` int NOT NULL DEFAULT 0, `10` int NOT NULL DEFAULT 0, `11` int NOT NULL DEFAULT 0, `12` int NOT NULL DEFAULT 0 );
CREATE TABLE `user_current_HTML` (`user_id` int NOT NULL PRIMARY KEY, `current` int NOT NULL DEFAULT 0);
CREATE TABLE `CSS` ( `id` int NOT NULL PRIMARY KEY, `course_id` int NOT NULL, `title` varchar(100) NOT NULL, `desc` text NOT NULL, `code` text, `img_count` int NOT NULL, `has_coding` varchar(10) NOT NULL);
CREATE TABLE `CSS_Coding` ( `course_id` int NOT NULL PRIMARY KEY, `instruction` text NOT NULL );
CREATE TABLE `user_CSS_circumstances` ( `user_id` int NOT NULL PRIMARY KEY, `1` int NOT NULL DEFAULT 0, `2` int NOT NULL DEFAULT 0, `3` int NOT NULL DEFAULT 0, `4` int NOT NULL DEFAULT 0, `5` int NOT NULL DEFAULT 0, `6` int NOT NULL DEFAULT 0, `7` int NOT NULL DEFAULT 0, `8` int NOT NULL DEFAULT 0, `9` int NOT NULL DEFAULT 0);
CREATE TABLE `user_current_CSS` (`user_id` int NOT NULL PRIMARY KEY, `current` int NOT NULL DEFAULT 0);
